INSERT INTO `gd_media` VALUES ('150509194356EPRYZCW', '1', null, null, '/wxadmin/upload/media/150512175218LAAUXIN.jpeg', null, null, '0', null, null, '5'), ('150509194448NUURAIG', '1', null, null, '/wxadmin/upload/media/150509194458KMUYHAV.mp3', null, 'hsPuUL-aal-agbYC2p_vMXlEkxQ3j1vzKy0HOapm9uTt4bFMyyFXKQOkBWGobE6m', '0', null, null, '3'), ('150509204726OYXMFKF', '1', null, null, '/wxadmin/upload/media/150512173007KTREKUJ.mp4', null, 'jbS2-GTDzfdE8UDChhv4rbw0KL6ZvG9AiEFZJ4uRZoZsz9JHqiQRsqHRWF82a3tf', '0', null, null, '4'), ('150512172219PMTBXJP', '1', '厦门长塑磁驱同步双拉薄膜填补国际高端尼龙薄膜市场空白', '长塑发布', '/wxadmin/upload/media/150512173323MFKTHCJ.jpeg', '<p style=\"text-align: left; text-indent: 2em;\"><span style=\"font-family: 微软雅黑; text-indent: 38px;\">近日，厦门长塑实业有限公司引进的世界最先进的磁悬浮线性电机同步拉伸尼龙生产线成功投产！</span></p><p style=\"text-align:center;text-indent:16px\"><img src=\"http://wxpay.zlgame.cn/wxadmin/ueditor/jsp/upload/image/20150512/1431425112253049823.jpg\" title=\"1431425112253049823.jpg\" alt=\"title_img.jpg\"/></p><p style=\"text-indent: 2em;\"><span style=\"font-size:16px;font-family:微软雅黑\">该项目实现了世界最先进的磁悬浮驱动技术在</span><span style=\"font-size:16px;font-family:微软雅黑\">BOPA</span><span style=\"font-size:16px;font-family:微软雅黑\">薄膜生产线上的首次使用，</span><span style=\"font-size:16px;font-family:微软雅黑\">由于设备的精密控制和调整的灵活性，生产的</span><span style=\"font-size:16px;font-family:微软雅黑\">BOPA</span><span style=\"font-size:16px;font-family:微软雅黑\">质量更稳定同时可以依照客户的需求定制个性化产品</span><span style=\"font-size:16px;font-family:微软雅黑\">，是重要的历史性突破！</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size:16px;font-family:微软雅黑\">磁悬浮线性同步拉伸工艺的拉伸阶段每个链铗都由单独的线性电机控制，且拉伸比可调、轨道可调。磁悬浮线性电机同步拉伸工艺取以往分步拉伸法和机械同步拉伸法之长，制成的线性同步薄膜集机械强度好、延展性好与均衡性好等优点于一身，填补了国内外高端同步拉伸尼龙薄膜市场空白，市场前景非常广阔！</span></p><p style=\"margin: 2px 0px 0px; text-indent: 2em; line-height: 21px;\"><span style=\"font-family:微软雅黑\">作为全球最大的</span><span style=\"font-family:微软雅黑\">BOPA</span><span style=\"font-family:微软雅黑\">双向拉伸尼龙薄膜生产商，厦门长塑实业有限公司经过多年发展与积淀，整合全球先进资源与技术不断创新，致力于实现</span><span style=\"font-family:微软雅黑\">“</span><span style=\"font-family: 微软雅黑\">成为世界级绿色生活材料的引领者</span><span style=\"font-family:微软雅黑\">”</span><span style=\"font-family:微软雅黑\">的企业愿景。</span></p><p style=\"margin-top:2px;margin-right:0;margin-bottom:0;margin-left:0;margin-bottom:0;text-indent:32px;line-height:21px\"><span style=\"font-family:微软雅黑\">&nbsp;</span></p><p style=\"text-indent:32px\"><span style=\"font-size:16px;font-family:微软雅黑\">&nbsp;</span></p><p style=\"text-indent: 2em;\"><a name=\"_GoBack\"></a><span style=\"font-size:16px;font-family:微软雅黑\">磁驱动线性同步生产线的成功投产，标志着长塑实业带着世界最先进的技术，站在了</span><span style=\"font-size:16px;font-family:微软雅黑\">BOPA</span><span style=\"font-size:16px;font-family:微软雅黑\">膜行业的前端，并由此全面打开产品格局，充分展现了行业引领者的风范。</span></p><p style=\"text-indent: 2em;\"><span style=\"font-size:16px;font-family:微软雅黑\">“</span><span style=\"font-size:16px;font-family:微软雅黑\">每一次汗水和泪水是故事背后的精彩，每一次激情和梦想正是对肯定的渴望。</span><span style=\"font-size:16px;font-family:微软雅黑\">”</span><span style=\"font-size:16px;font-family:微软雅黑\">长塑领导人在磁悬浮线性电机同步生产线投产庆功晚宴上如是说：</span><span style=\"font-size:16px;font-family:微软雅黑\">“</span><span style=\"font-size:16px;font-family:微软雅黑\">终点是另一个高度的起点！</span><span style=\"font-size:16px;font-family:微软雅黑\">”</span><span style=\"font-size:16px;font-family:微软雅黑\">我们期盼以长塑为代表的国内</span><span style=\"font-size:16px;font-family:微软雅黑\">BOPA</span><span style=\"font-size:16px;font-family:微软雅黑\">薄膜制造企业抓住国家</span><span style=\"font-size:16px;font-family:微软雅黑\">“</span><span style=\"font-size:16px;font-family:微软雅黑\">一带一路</span><span style=\"font-size:16px;font-family:微软雅黑\">”</span><span style=\"font-size:16px;font-family:微软雅黑\">战略大力推进的大好历史机遇</span><span style=\"font-size:16px;font-family:微软雅黑\">，用百分百的拼搏和创新</span><span style=\"font-size:16px;font-family:微软雅黑\">,</span><span style=\"font-size:16px;font-family:微软雅黑\">为中国</span><span style=\"font-size:16px;font-family:微软雅黑\">BOPA</span><span style=\"font-size:16px;font-family:微软雅黑\">薄膜事业的发展做出应有贡献，带来更多跨越性飞跃。</span></p><p>&nbsp;</p><p><br/></p>', '8KRrlpYMUhwIqWbRScI_SClbSYCRN0II-j7gmuRQ_BkoLh8UDimsf7Mo0Gdmn3-6', '0', '', '近日，厦门长塑实业有限公司引进的世界最先进的磁悬浮线性电机同步拉伸尼龙生产线成功投产！', '1');
COMMIT;

